  @keyframes showAndBounce {
      0% {
        top: -100px;
        opacity: 0;
        visibility: hidden;
        transform: translateY(-100%) scale(0.5);
      }
      50% {
        opacity: 1;
        visibility: visible;
        transform: translateY(-50px) scale(1) ;
